import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from "./api"; // Assuming React Router is being used

function decodeToken(token) {
  if (!token) {
    return null;
  }

  const tokenParts = token.split('.');

  if (tokenParts.length !== 3) {
    throw new Error('Invalid token format');
  }

  const payload = atob(tokenParts[1]);
  const decodedToken = JSON.parse(payload);
  return decodedToken;
}

function Flights() {
  const [isAdmin, setIsAdmin] = useState(false);
  const history = useNavigate();
  const [flight, setFlight] = useState({
    flight_id: 0,
    airline: '',
    flight_number: '',
    departure_airport: '',
    departure_city: '',
    departure_time: '',
    arrival_airport: '',
    arrival_city: '',
    arrival_time: '',
    duration: '',
    price: '',
    available_seats: '',
    aircraft_type: '',
    flight_class: '',
    stopovers: '',
    booking_info: '',
    images: ''
  });

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from wherever it's stored

    if (!token) {
      // Redirect the user to the login page if no token is found
      history.push('/login');
    } else {
      try {
        const decodedToken = decodeToken(token);
        // Check if the decoded token indicates the user is an admin
        if (decodedToken && decodedToken.role === 'admin') {
          setIsAdmin(true);
        } else {
          setIsAdmin(false);
          // Redirect to a different page or show a message for non-admin users
        }
      } catch (error) {
        console.error('Error decoding token:', error.message);
        // Handle token decoding error (e.g., redirect to login)
        history('/login');
      }
    }
  }, [history]);

  // Functions to handle admin-only endpoints
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFlight({ ...flight, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isAdmin) {
      // Prevent non-admin users from submitting the form
      return;
    }

    // Make API call to add_flight endpoint
    // Use proper API handling methods (fetch, axios, etc.) here
    try {
  const response = await api.post('/admin/add_flight/', flight, {
    headers: {
      'Content-Type': 'application/json',
      // Include authorization token in headers for authenticated requests
      Authorization: `Bearer ${localStorage.getItem('token')}`,
    },
  });
  console.log('Flight added successfully:', response.data); // Handle success response
} catch (error) {
  console.error('Error adding flight:', error);
  // Handle error
}
  };

  return (
  <form onSubmit={handleSubmit}>
    <label>
      Airline:
      <input
        type="text"
        name="airline"
        value={flight.airline}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Flight Number:
      <input
        type="text"
        name="flight_number"
        value={flight.flight_number}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Departure Airport:
      <input
        type="text"
        name="departure_airport"
        value={flight.departure_airport}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Departure City:
      <input
        type="text"
        name="departure_city"
        value={flight.departure_city}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Departure Time:
      <input
        type="text"
        name="departure_time"
        value={flight.departure_time}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Arrival Airport:
      <input
        type="text"
        name="arrival_airport"
        value={flight.arrival_airport}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Arrival City:
      <input
        type="text"
        name="arrival_city"
        value={flight.arrival_city}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Arrival Time:
      <input
        type="text"
        name="arrival_time"
        value={flight.arrival_time}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Duration:
      <input
        type="text"
        name="duration"
        value={flight.duration}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Price:
      <input
        type="text"
        name="price"
        value={flight.price}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Available Seats:
      <input
        type="text"
        name="available_seats"
        value={flight.available_seats}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Aircraft Type:
      <input
        type="text"
        name="aircraft_type"
        value={flight.aircraft_type}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Flight Class:
      <input
        type="text"
        name="flight_class"
        value={flight.flight_class}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Stopovers:
      <input
        type="text"
        name="stopovers"
        value={flight.stopovers}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Booking Info:
      <input
        type="text"
        name="booking_info"
        value={flight.booking_info}
        onChange={handleInputChange}
      />
    </label>

    <label>
      Images:
      <input
        type="text"
        name="images"
        value={flight.images}
        onChange={handleInputChange}
      />
    </label>

    {/* Disabled input for flight_id */}
    <label>
      Flight ID:
      <input type="text" name="flight_id" value={flight.flight_id} disabled />
    </label>

    <button type="submit">Submit</button>
  </form>
);

}

export default Flights;
