                  value={flight.departure_city}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Departure Time:
                <input
                  type="text"
                  name="departure_time"
                  value={flight.departure_time}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
            <label className="label">
              Arrival Airport:
              <input
                type="text"
                name="arrival_airport"
                value={flight.arrival_airport}
                onChange={handleInputChange}
                className="text_box"
              />
            </label>
            </div>
            <div>
            <label className="label">
              Arrival City:
              <input
                type="text"
                name="arrival_city"
                value={flight.arrival_city}
                onChange={handleInputChange}
                className="text_box"
              />
            </label>
            </div>
            <div>
            <label className="label">
              Arrival Time:
              <input
                type="text"
                name="arrival_time"
                value={flight.arrival_time}
                onChange={handleInputChange}
                className="text_box"
              />
            </label>
            </div>
            <div>
              <label className="label">
                Duration:
                <input
                  type="text"
                  name="duration"
                  value={flight.duration}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Price:
                <input
                  type="text"
                  name="price"
                  value={flight.price}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Available Seats:
                <input
                  type="text"
                  name="available_seats"
                  value={flight.available_seats}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Aircraft Type:
                <input
                  type="text"
                  name="aircraft_type"
                  value={flight.aircraft_type}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Flight Class:
                <input
                  type="text"
                  name="flight_class"
                  value={flight.flight_class}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Stopovers:
                <input
                  type="text"
                  name="stopovers"
                  value={flight.stopovers}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Booking Info:
                <input
                  type="text"
                  name="booking_info"
                  value={flight.booking_info}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Images:
                <input
                  type="text"
                  name="images"
                  value={flight.images}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <button className="flights_submit_button" type="submit">Submit</button>
          </form>
        </div>
      </div>
);
}

export default Flights;
