import { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'; // Assuming React Router is being used

function AdminPage() {
  const [isAdmin, setIsAdmin] = useState(false);
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from wherever it's stored

    if (!token) {
      // Redirect the user to the login page if no token is found
      history.push('/login');
    } else {
      try {
        const decodedToken = decodeToken(token);
        // Check if the decoded token indicates the user is an admin
        if (decodedToken && decodedToken.role === 'admin') {
          setIsAdmin(true);
        } else {
          setIsAdmin(false);
          // Redirect to a different page or show a message for non-admin users
        }
      } catch (error) {
        console.error('Error decoding token:', error.message);
        // Handle token decoding error (e.g., redirect to login)
        history.push('/login');
      }
    }
  }, [history]);

  // Functions to handle admin-only endpoints
  const addFlight = async (flightData) => {
    if (!isAdmin) {
      // Prevent non-admin users from accessing this endpoint
      return;
    }
    // Make API call to add_flight endpoint
    // Use proper API handling methods (fetch, axios, etc.) here
    try {
      const response = await fetch('/api/add_flight/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Include authorization token in headers for authenticated requests
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify(flightData),
      });
      const data = await response.json();
      console.log(data); // Handle response data
    } catch (error) {
      console.error('Error adding flight:', error);
      // Handle error
    }
  };

  return (
    <div>
      {/* Your React components/UI for the admin page */}
      {isAdmin && (
        <>
          {/* Render admin-only components or provide access to admin features */}
          {/* For example: buttons to add, delete, update flights */}
        </>
      )}
    </div>
  );
}

export default AdminPage;
