import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import api from './api';

const HotelDetail = () => {
  const location = useLocation();
  const [hotelData, setHotelData] = useState(null);

  useEffect(() => {
    const fetchHotelData = async () => {
      try {
        const hotelName = decodeURIComponent(location.pathname.split('/').pop());
        const response = await api.get(`/hotels/${hotelName}`);
        if (Array.isArray(response.data) && response.data.length > 0) {
          setHotelData(response.data[0]); // Assuming the response is an array with a single object
        } else {
          throw new Error('Hotel data not found');
        }
      } catch (error) {
        console.error('Error fetching hotel data:', error);
      }
    };

    fetchHotelData();
  }, [location.pathname]);

  return (
    <div>
      {hotelData ? (
        <div>
          <h1>{hotelData.hotel_name}</h1>
          <p>City: {hotelData.location_city}</p>
          <p>Country: {hotelData.location_country}</p>
          <p>Address: {hotelData.address}</p>
          {/* Add additional information as needed */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default HotelDetail;
