import { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from "./api"; // Assuming React Router is being used

function Flights() {
  const [isAdmin, setIsAdmin] = useState(false);
  const history = useHistory();
  const [flight, setFlight] = useState({
    flight_id: 0,
    airline: '',
    flight_number: '',
    departure_airport: '',
    departure_city: '',
    departure_time: '',
    arrival_airport: '',
    arrival_city: '',
    arrival_time: '',
    duration: '',
    price: '',
    available_seats: '',
    aircraft_type: '',
    flight_class: '',
    stopovers: '',
    booking_info: '',
    images: ''
  });

  useEffect(() => {
    const token = localStorage.getItem('token'); // Retrieve token from wherever it's stored

    if (!token) {
      // Redirect the user to the login page if no token is found
      history.push('/login');
    } else {
      try {
        const decodedToken = decodeToken(token);
        // Check if the decoded token indicates the user is an admin
        if (decodedToken && decodedToken.role === 'admin') {
          setIsAdmin(true);
        } else {
          setIsAdmin(false);
          // Redirect to a different page or show a message for non-admin users
        }
      } catch (error) {
        console.error('Error decoding token:', error.message);
        // Handle token decoding error (e.g., redirect to login)
        history.push('/login');
      }
    }
  }, [history]);

  // Functions to handle admin-only endpoints
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFlight({ ...flight, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isAdmin) {
      // Prevent non-admin users from submitting the form
      return;
    }

    // Make API call to add_flight endpoint
    // Use proper API handling methods (fetch, axios, etc.) here
    try {
      const response = await api.post('/admin/add_flight/', {
        headers: {
          'Content-Type': 'application/json',
          // Include authorization token in headers for authenticated requests
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Flight added successfully:', response.data); // Handle success response
    } catch (error) {
      console.error('Error adding flight:', error);
      // Handle error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Input fields for flight details */}
      {/* Add similar input fields for each flight detail */}
      <label>
        Airline:
        <input type="text" name="airline" value={flight.airline} onChange={handleInputChange} />
      </label>
      {/* ... other input fields ... */}

      {/* Disabled input for flight_id */}
      <label>
        Flight ID:
        <input type="text" name="flight_id" value={flight.flight_id} disabled />
      </label>

      <button type="submit">Submit</button>
    </form>
  );
}

export default Flights;
