              <li><Link to="/Account">ACCOUNT</Link></li>
            </ul>
            <Link to="/Login" className="register-btn">Log In</Link>
        </nav>

          <div>
            <nav className="action-navbar">
              <ul className="flights-navbar">
                <li><a href="#add_flights">Add Flights</a></li>
                <li><a href="#delete_flights">Delete Flights</a></li>
                {/* Add other action links as needed */}
              </ul>
            </nav>
          </div>

          <div>
      <h1>All Flights</h1>
      <table>
        <thead>
          <tr>
            <th>Airline</th>
            <th>Flight Number</th>
            {/* Add more table headers for other fields */}
          </tr>
        </thead>
        <tbody>
          {flights.map((flight, index) => (
            <tr key={index}>
              <td>{flight.airline}</td>
              <td>{flight.flight_number}</td>
              {/* Add more table cells for other fields */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>

        <div className="fligth_form">
              <form id="AddFlightForm" className="fligths_form" onSubmit={handleSubmit}>
            <div>
            <label className="label">
              Airline:
              <input
                type="text"
                name="airline"
                className="text_box"
                value={flight.airline}
                onChange={handleInputChange}

              />
            </label>
            </div>
            <div>
              <label className="label">
                Flight Number:
                <input
                  type="text"
                  name="flight_number"
                  className="text_box"
                  value={flight.flight_number}
                  onChange={handleInputChange}
                />
              </label>
            </div>
            <div>
            <label className="label">
              Departure Airport:
              <input
                type="text"
                name="departure_airport"
                value={flight.departure_airport}
