                  className="text_box"
                />
              </label>
            </div>
            <div>
              <label className="label">
                Departure Time:
                <input
                  type="text"
                  name="departure_time"
                  value={flight.departure_time}
                  onChange={handleInputChange}
                  className="text_box"
                />
              </label>
            </div>
            <div>
            <label className="label">
              Arrival Airport:
              <input
                type="text"
                name="arrival_airport"
                value={flight.arrival_airport}
                onChange={handleInputChange}
                className="text_box"
              />
            </label>
            </div>
            <div>
            <label className="label">
              Arrival City:
              <input
                type="text"
                name="arrival_city"
                value={flight.arrival_city}
                onChange={handleInputChange}
                className="text_box"
              />
            </label>
            </div>
            <div>
            <label className="label">
              Arrival Time:
              <input
                type="text"
                name="arrival_time"
